00059     public class Permutations : IEquatable<Permutations>
00060     {
00061         public int N { get; set; }
00062         public int K { get; set; }
00063         public List<int[]> Elements { get; set; }
00064 
00065         public Permutations(int n, int k)
00066         {
00067             N = n;
00068             K = k;
00069             Elements = new List<int[]>();
00070             int[] data = new int[k];
00071             for (int i = 0; i < k; ++i)
00072                 data[i] = i;
00073             Elements.Add(data);
00074 
00075             CalculatePermutations();
00076         }
00077 
00078         public Permutations(string key, List<int[]> elems)
00079         {
00080             string[] s = key.Split('_');
00081             N = Int32.Parse(s[0]);
00082             K = Int32.Parse(s[1]);
00083             Elements = elems;
00084         }
00085 
00086         public int[] Successor(int[] previous)
00087         {
00088             if (previous.Length == 0 || previous[0] == this.N - this.K)
00089                 return null;
00090 
00091             int[] next = new int[K];
00092 
00093             int i;
00094             for (i = 0; i < this.K; ++i)
00095                 next[i] = previous[i];
00096 
00097             for (i = this.K - 1; i > 0 && next[i] == this.N - this.K + i; --i) { }
00098 
00099             ++next[i];
00100 
00101             for (int j = i; j < this.K - 1; ++j)
00102                 next[j + 1] = next[j] + 1;
00103 
00104             return next;
00105         }
00106 
00107         public void CalculatePermutations()
00108         {
00109             int[] last = Elements[0];
00110             int[] next = Successor(last);
00111             while (next != null)
00112             {
00113                 Elements.Add(next);
00114                 last = next;
00115                 next = Successor(last);
00116             }
00117         }
00118 
00119         public bool Equals(Permutations other)
00120         {
00121             return (N == other.N && K == other.K);
00122         }
00123     }
