00177         public static double CalculatePeptideMass(string sequence, bool mono)
00178         {
00179             double pepMass = Chemicals["H"].MonoMass * 2 + Chemicals["O"].MonoMass;
00180             if (!mono)
00181                 pepMass = Chemicals["H"].AvgMass * 2 + Chemicals["O"].AvgMass;
00182             for (int i = 0; i < sequence.Length; ++i)
00183             {
00184                 pepMass += AminoAcids[sequence[i]].Mass(mono);
00185             }
00186             return pepMass;
00187         }
