00210         public static Permutations GetAscendingPermutations(int n, int k)
00211         {
00212             string key = n + "_" + k;
00213             lock (Combinations)
00214             {
00215                 if (Combinations.ContainsKey(key))
00216                     return Combinations[key];
00217                 else
00218                 {
00219                     Permutations m = new Permutations(n, k);
00220                     lock (Combinations)
00221                     {
00222                         if (!Combinations.ContainsKey(key))
00223                         {
00224                             Combinations.Add(key, m);
00225                         }
00226                     }
00227                     return m;
00228                 }
00229             }
00230         }
