00464         static bool InRange(double v, double min, double max)
00465         {
00466             return ((v >= min) && (v <= max));
00467         }
00468 
00469         static bool GetUniqueIonsSorted(out double[] outIonsNoNL, out IonWithNL[] outIonsWithNL, List<double> ionsNoNL, List<IonWithNL> ionsWithNL)
00470         {
00471             outIonsNoNL = null;
00472             outIonsWithNL = null;
00473 
00474             if (ionsNoNL.Count > 0)
00475             {
00476                 double[] aIonsNoNL = new double[ionsNoNL.Count];
00477                 for (int i = 0; i < aIonsNoNL.Length; ++i)
00478                 {
00479                     aIonsNoNL[i] = IonMassStabilizer.Stabilize(ionsNoNL[i]);
00480                 }
00481                 Array.Sort(aIonsNoNL);
00482                 //Removing duplicates from array
00483                 int k1 = 0, k2 = 1;
00484                 for (; k2 < aIonsNoNL.Length; ++k2)
00485                 {
00486                     if (aIonsNoNL[k1] < aIonsNoNL[k2])
00487                     {
00488                         ++k1;
00489                     }
00490                     else
00491                     {
00492                         ++k2;
00493                         break;
00494                     }
00495                 }
00496 
00497                 for (; k2 < aIonsNoNL.Length; ++k2)
00498                 {
00499                     if (aIonsNoNL[k1] < aIonsNoNL[k2])
00500                     {
00501                         ++k1;
00502                         aIonsNoNL[k1] = aIonsNoNL[k2];
00503                     }
00504                 }
00505 
00506                 Array.Resize(ref aIonsNoNL, ++k1);
00507                 outIonsNoNL = aIonsNoNL;
00508             }
00509 
00510             if (null != ionsWithNL && ionsWithNL.Count > 0)
00511             {
00512                 IonWithNL[] aIonsWithNL = ionsWithNL.ToArray();
00513                 FastSort(aIonsWithNL);
00514                 {
00515                     //Merging duplicates from array
00516                     int k1 = 0, k2 = 1;
00517                     for (; k2 < aIonsWithNL.Length; ++k2)
00518                     {
00519                         if (aIonsWithNL[k1].Mz < aIonsWithNL[k2].Mz)
00520                         {
00521                             ++k1;
00522                         }
00523                         else
00524                         {
00525                             aIonsWithNL[k1].AddRange(aIonsWithNL[k2]);
00526                             ++k2;
00527                             break;
00528                         }
00529                     }
00530 
00531                     for (; k2 < aIonsWithNL.Length; ++k2)
00532                     {
00533                         if (aIonsWithNL[k1].Mz < aIonsWithNL[k2].Mz)
00534                         {
00535                             ++k1;
00536                             aIonsWithNL[k1] = aIonsWithNL[k2];
00537                         }
00538                         else
00539                         {
00540                             aIonsWithNL[k1].AddRange(aIonsWithNL[k2]);
00541                         }
00542                     }
00543 
00544                     Array.Resize(ref aIonsWithNL, ++k1);
00545                     outIonsWithNL = aIonsWithNL;
00546                 }
00547             }
00548 
00549             if (null != outIonsNoNL || null != outIonsWithNL)
00550             {
00551                 if (null == outIonsNoNL)
00552                 {
00553                     outIonsNoNL = Array.Empty<double>();
00554                 }
00555                 return true;
00556             }
00557             return false;
00558         }
