00560         private static void doQuicksort(IonWithNL[] elements, int left, int right)
00561         {
00562             int i = left, j = right;
00563             //middle pivot; Should avoid n^2 worst case for nearly sorted array
00564             IonWithNL pivot = elements[left + (right - left) / 2];
00565 
00566             while (i <= j)
00567             {
00568                 while (elements[i].Mz < pivot.Mz)
00569                 {
00570                     i++;
00571                 }
00572 
00573                 while (elements[j].Mz > pivot.Mz)
00574                 {
00575                     j--;
00576                 }
00577 
00578                 if (i <= j)
00579                 {
00580                     // Swap
00581                     //IonWithNL tmp = elements[i];
00582                     //elements[i] = elements[j];
00583                     //elements[j] = tmp;
00584 
00585                     (elements[i], elements[j]) = (elements[j], elements[i]);
00586 
00587                     i++;
00588                     j--;
00589                 }
00590             }
00591 
00592             // Recursive calls
00593             if (left < j)
00594             {
00595                 doQuicksort(elements, left, j);
00596             }
00597 
00598             if (i < right)
00599             {
00600                 doQuicksort(elements, i, right);
00601             }
00602         }
00603 
00604         public static void FastSort(IonWithNL[] ions)
00605         {
00606             if (ions.Length > 1)
00607             {
00608                 doQuicksort(ions, 0, ions.Length - 1);
00609             }
00610         }
