00612         public static void CalculateAllInternalCombinations(double lastMassInternal, 
00613                                                             int i,
00614                                                             List<double> ionsNoNL,
00615                                                             byte[] seq, 
00616                                                             Modification[] mods,
00617                                                             double lowerBound, 
00618                                                             double upperBound, 
00619                                                             bool mono,
00620                                                             AminoAcid[] aminoAcids)
00621         {
00622             double currentMass = lastMassInternal;
00623             for (int k = seq.Length - 2; k > i + 2; --k)
00624             {
00625                 currentMass -= aminoAcids[(char) seq[k]].Mass(mono);
00626 
00627                 if (mods[k + 1] != null)
00628                 {
00629                     currentMass -= mods[k + 1].Mass(mono);
00630                 }
00631                 if (InRange(currentMass, lowerBound, upperBound))
00632                 {
00633                     ionsNoNL.Add(currentMass);
00634                 }
00635             }
00636         }
