00638         public static void CalculateIon(int charge, List<double> ionsNoNL, List<IonWithNL> ionsWithNL, int possibleAmoniaLosses,
00639                                         int possibleWaterLosses, double massO, double massH, double massC, double massN, double massProton,
00640                                         List<double> losses, InstrumentSetting setting, double ion,
00641                                         double lowerBound, double upperBound, string maxAllowedChargeState)
00642         {
00643             //add possible neutral losses of modifications
00644             for (int i = 0; i < losses.Count; ++i)
00645             {
00646                 double newMass = ion - losses[i];
00647                 if (InRange(newMass, lowerBound, upperBound))
00648                 {
00649                     //add water or ammonia losses
00650                     if ((setting.UseWaterLosses && possibleWaterLosses > 0) || (setting.UseAmmoniaLosses && possibleAmoniaLosses > 0))
00651                     {
00652                         IonWithNL ionWithNL = new IonWithNL(newMass);
00653                         ionsWithNL.Add(ionWithNL);
00654                         CalculatePossibleNeutralLosses(newMass, possibleWaterLosses, possibleAmoniaLosses,
00655                           1, setting.UseWaterLosses, setting.UseAmmoniaLosses, massO, massH, massC,
00656                           massN, ionWithNL, lowerBound, upperBound);
00657                     }
00658                     else
00659                     {
00660                         ionsNoNL.Add(newMass);
00661                     }
00662                 }
00663                 if (charge > 2)
00664                 {
00665                     CalculateHigherChargedIons(charge, ionsNoNL, ionsWithNL, possibleAmoniaLosses, possibleWaterLosses, massO, massH, massC, massN, massProton, setting, newMass, lowerBound, upperBound, maxAllowedChargeState);
00666                 }
00667             }
00668 
00669             if (losses.Count == 0)
00670             {
00671                 if (InRange(ion, lowerBound, upperBound))
00672                 {
00673                     //add water or ammonia losses
00674                     if ((setting.UseWaterLosses && possibleWaterLosses > 0) || (setting.UseAmmoniaLosses && possibleAmoniaLosses > 0))
00675                     {
00676                         IonWithNL ionWithNL = new IonWithNL(ion);
00677                         ionsWithNL.Add(ionWithNL);
00678                         CalculatePossibleNeutralLosses(ion, possibleWaterLosses, possibleAmoniaLosses,
00679                           1, setting.UseWaterLosses, setting.UseAmmoniaLosses, massO, massH, massC,
00680                           massN, ionWithNL, lowerBound, upperBound);
00681                     }
00682                     else
00683                     {
00684                         ionsNoNL.Add(ion);
00685                     }
00686                 }
00687                 if (charge > 2)
00688                 {
00689                     CalculateHigherChargedIons(charge, ionsNoNL, ionsWithNL, possibleAmoniaLosses, possibleWaterLosses, massO, massH, massC, massN, massProton, setting, ion, lowerBound, upperBound, maxAllowedChargeState);
00690                 }
00691             }
00692         }
00693 
00694         public static void CalculateHigherChargedIons(int charge, List<double> ionsNoNL, List<IonWithNL> ionsWithNL, int possibleAmoniaLosses,
00695                                                       int possibleWaterLosses, double massO, double massH, double massC, double massN, double massProton,
00696                                                       InstrumentSetting setting, double newMass, double lowerBound, double upperBound, string maxAllowedChargeState)
00697         {
00698             string allowedChargeStates = "+2";
00699             switch (maxAllowedChargeState)
00700             {
00701                 case "+2":
00702                     AddHigherChargedIons(ionsNoNL, ionsWithNL, possibleAmoniaLosses, possibleWaterLosses,
00703                       massO, massH, massC, massN, massProton, setting.UseWaterLosses, setting.UseAmmoniaLosses,
00704                       newMass, 1, lowerBound, upperBound);
00705                     break;
00706                 case "+3":
00707                     for (int i = 1; i < charge - 1 && i < 3; ++i)
00708                     {
00709                         AddHigherChargedIons(ionsNoNL, ionsWithNL, possibleAmoniaLosses, possibleWaterLosses,
00710                           massO, massH, massC, massN, massProton, setting.UseWaterLosses, setting.UseAmmoniaLosses,
00711                           newMass, i, lowerBound, upperBound);
00712                     }
00713                     break;
00714                 case "+4":
00715                     for (int i = 1; i < charge - 1 && i < 4; ++i)
00716                     {
00717                         AddHigherChargedIons(ionsNoNL, ionsWithNL, possibleAmoniaLosses, possibleWaterLosses,
00718                           massO, massH, massC, massN, massProton, setting.UseWaterLosses, setting.UseAmmoniaLosses,
00719                           newMass, i, lowerBound, upperBound);
00720                     }
00721                     break;
00722                 case "Precursor - 1":
00723                     for (int i = 1; i < charge - 1; ++i)
00724                     {
00725                         AddHigherChargedIons(ionsNoNL, ionsWithNL, possibleAmoniaLosses, possibleWaterLosses,
00726                           massO, massH, massC, massN, massProton, setting.UseWaterLosses, setting.UseAmmoniaLosses,
00727                           newMass, i, lowerBound, upperBound);
00728                     }
00729                     break;
00730             }
00731         }
00732 
00733         public static void AddHigherChargedIons(List<double> ionsNoNL, List<IonWithNL> ionsWithNL, int possibleAmoniaLosses,
00734                                                 int possibleWaterLosses, double massO, double massH, double massC, double massN,
00735                                                 double massProton, bool water, bool ammonia, double ion, int currentCharge,
00736                                                 double lowerBound, double upperBound)
00737         {
00738             double chargedIon = ion + currentCharge * massProton;
00739             ++currentCharge;
00740 
00741             double myMass = chargedIon / currentCharge;
00742             if (InRange(myMass, lowerBound, upperBound))
00743             {
00744                 if ((water && possibleWaterLosses > 0) || (ammonia && possibleAmoniaLosses > 0))
00745                 {
00746                     IonWithNL ionWithNL = new IonWithNL(myMass);
00747                     ionsWithNL.Add(ionWithNL);
00748                     CalculatePossibleNeutralLosses(chargedIon, possibleWaterLosses, possibleAmoniaLosses,
00749                       currentCharge, water, ammonia, massO, massH,
00750                       massC, massN, ionWithNL, lowerBound, upperBound);
00751                 }
00752                 else
00753                 {
00754                     ionsNoNL.Add(myMass);
00755                 }
00756             }
00757         }
00758 
00759         public static void CalculatePossibleNeutralLosses(double ionMass, int possibleWaterLosses,
00760                                                           int possibleAmoniaLosses, int charge, bool water, bool ammonia, double massO, double massH,
00761                                                           double massC, double massN, IonWithNL ionWithNL,
00762                                                           double lowerBound, double upperBound)
00763         {
00764             //handle neutral losses
00765 
00766             double massWaterLoss = massH * 2 + massO;
00767             double massAmoniaLoss = massH * 3 + massN;
00768 
00769             int currentWaterLoss = 0;
00770             while (water && currentWaterLoss < possibleWaterLosses)
00771             {
00772                 ++currentWaterLoss;
00773                 double chargedIon = ionMass - currentWaterLoss * massWaterLoss;
00774                 double myMass = IonMassStabilizer.Stabilize(chargedIon / charge);
00775                 //if(InRange( myMass, lowerBound, upperBound ))
00776                 ionWithNL.Add(myMass);
00777             }
00778 
00779             int currentAmoniaLoss = 0;
00780             while (ammonia && currentAmoniaLoss < possibleAmoniaLosses)
00781             {
00782                 ++currentAmoniaLoss;
00783                 double chargedIon = ionMass - currentAmoniaLoss * massAmoniaLoss;
00784                 double myMass = IonMassStabilizer.Stabilize(chargedIon / charge);
00785                 //if(InRange( myMass, lowerBound, upperBound ))
00786                 ionWithNL.Add(myMass);
00787             }
00788         }
00789     }
