00802 
00803     internal static class IonMassStabilizer
00804     {
00805         const int SignificantBitsStabilizer = 47; // Approximately 14 decimal significant digits
00806 
00807         const ulong RoundingAdditionStabilizer = unchecked(1uL << (52 - SignificantBitsStabilizer - 1));
00808         const ulong ClearBitsStabilizerMask = unchecked(0xFFFFFFFFFFFFFFFFuL << (52 - SignificantBitsStabilizer));
00809 
00810         /*
00811         Implements super-fast base2 nearest-value rounding of double precision values
00812         to specified number of significant 2-base digits 
00813         Utilizes specificity of IEEE 754 double-precision format
00814         Works correctly in all cases except double.Max, NaN, +/-Inf etc in input
00815         Specially tested correctness when 52bit mantissa is all 1 (0xFFFFFFFFFFFFF)
00816         By benchmarks works 7-15 times faster than Math.Round()
00817         */
00818         static internal double Stabilize(double value)
00819         {
00820             #if !DISABLE_FP_STABILIZER
00821             unchecked
00822             {
00823                 ulong im = (RoundingAdditionStabilizer + (ulong)BitConverter.DoubleToInt64Bits(value)) & ClearBitsStabilizerMask;
00824                 return BitConverter.Int64BitsToDouble((long)im);
00825             }
00826             #else
00827                 return value;
00828             #endif
00829         }
00830     }
